'FUNCTION cohere_chat( prompt VARCHAR2, documents JSON_ARRAY_T, chatHistory JSON_ARRAY_T ) ',
'RETURN VARCHAR2 IS ',
'    resp DBMS_CLOUD_TYPES.resp; ',
'    json_response CLOB; ',
'    s VARCHAR2(32767); ',
'    je JSON_ELEMENT_T;',
'    jo JSON_OBJECT_T;',
'    chatRequest JSON_OBJECT_T;',
'begin',
'  je := JSON_ELEMENT_T.parse(''{',
'"servingMode": {',
'    "modelId": "cohere.command-r-16k",',
'    "servingType": "ON_DEMAND"',
'},',
'"chatRequest": {',
'    "maxTokens": 600,',
'    "temperature": 0,',
'    "preambleOverride": null,',
'    "frequencyPenalty": 0,',
'    "presencePenalty": 0,',
'    "frequencyPenalty": 0,',
'    "topP": 0.75,',
'    "topK": 0,',
'    "isStream": false,',
'    "chatHistory": "[]",',
'    "apiFormat": "COHERE"',
'}',
'}'');',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''compartmentId'', compartment_ocid);',
'  chatRequest := jo.get_Object(''chatRequest'');',
'  chatRequest.put(''documents'', documents);',
'  chatRequest.put(''chatHistory'', chatHistory);',
'  chatRequest.put(''message'', prompt);',
'  log( ''<chat> request'', je.to_string);',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => oci_credential, ',
'        uri =>''https://inference.generativeai.''|| region || ''.oci.oraclecloud.com/20231130/actions/chat'',',
'        method => ''POST'', ',
'        body => UTL_RAW.cast_to_raw(je.to_string)                                                                                         ',
'    ); ',
'  json_response := DBMS_CLOUD.get_response_text(resp); ',
'  log( ''<chat> response'', json_response);',
'  APEX_UTIL.SET_SESSION_STATE( ''P2_CHAT_RESPONSE'', json_response );',
'  -- SELECT LISTAGG(text, '', '') WITHIN GROUP (ORDER BY ROWNUM) ',
