'FUNCTION get_query( p_type VARCHAR2 ) return VARCHAR2 is',
'    q varchar2(32767);',
'    about varchar2(32767);',
'BEGIN',
'    if p_type is null then',
'        q := ''SELECT rawtohex(id) chunck_id, doc_id, filename, path, TO_CHAR(content) content_char, content_type, region, page, summary, 1 score FROM docs_chunck'';',
'    elsif p_type = ''generate'' then',
'        q := ''SELECT rawtohex(id) chunck_id, doc_id, filename, path, TO_CHAR(content) content_char, content_type, region, page, summary, 1 score FROM docs_chunck where id=-999'';',
'    elsif p_type = ''search'' then',
'        q := q''^',
'        SELECT rawtohex(id) chunck_id, doc_id, filename, path, TO_CHAR(content) content_char, content_type, region, page, summary, score(99) score FROM docs_chunck',
'        WHERE CONTAINS(content, :P14_ABOUT, 99)>0 order by score(99) DESC FETCH FIRST 10 ROWS ONLY',
