'        if p_type = ''semantic'' then',
'            q := q''^',
'            SELECT id, doc_id, filename, path, TO_CHAR(content) content_char, content_type, region, page, summary, cohere_embed <=> :P14_VECTOR score FROM docs_chunck',
'            ORDER BY score FETCH FIRST 10 ROWS ONLY',
'            ^'';',
'        elsif p_type = ''hybrid'' or p_type = ''rag'' then',
'            -- Oracle Text score: 0 - 100.0 (higher is better)',
'            -- Vector distance : 0 - 1.0 (closer is better)',
'            q := q''^',
'            WITH text_search AS (',
'                SELECT id, score(99)/100 as score FROM docs_chunck',
'                WHERE CONTAINS(content, :P14_ABOUT, 99)>0 order by score(99) DESC FETCH FIRST 10 ROWS ONLY',
'            ),',
'            vector_search AS (',
'                SELECT id, cohere_embed <=> :P14_VECTOR AS vector_distance',
'                FROM docs_chunck',
'            )',
'            SELECT rawtohex(o.id) chunck_id, o.doc_id, o.filename, o.path, TO_CHAR(content) content_char, o.content_type, o.region, o.page, o.summary,',
'                (0.3 * ts.score + 0.7 * (1 - vs.vector_distance)) AS score',
'            FROM docs_chunck o',
'            JOIN text_search ts ON o.id = ts.id',
'            JOIN vector_search vs ON o.id = vs.id',
'            ORDER BY score DESC',
'            FETCH FIRST 10 ROWS ONLY',
'            ^'';',
'        end if;',
'    end if;',
'    -- LangChain Mode',
'    q := replace( q, ''docs_chunck'', ''v_docs_chunck'');',
'    return q;',
'end get_query;',
'',
'function get_prompt return varchar2 is',
'   p14_query varchar2(32767);',
'   p14_about varchar2(32767);',
'   p14_question varchar2(32767);',
'   p14_vector varchar2(32767);',
'   bfirst boolean;',
'   ret NUMBER;',
'   prompt varchar2(32767);',
'   cur number;',
'   r_filename varchar2(4096);',
'   r_path varchar2(4096);',
'   r_content_char varchar2(32767);',
'BEGIN ',
'   p14_query   := APEX_UTIL.GET_SESSION_STATE( ''P14_QUERY'' );',
'   p14_about   := APEX_UTIL.GET_SESSION_STATE( ''P14_ABOUT'' );',
'   p14_question:= APEX_UTIL.GET_SESSION_STATE( ''P14_QUESTION'' );',
'   p14_vector  := APEX_UTIL.GET_SESSION_STATE( ''P14_VECTOR'' );',
'',
'   cur := dbms_sql.open_cursor;',
'   dbms_sql.parse(cur,p14_query,dbms_sql.native);',
'   DBMS_SQL.DEFINE_COLUMN(cur, 1, r_filename,4096); ',
'   DBMS_SQL.DEFINE_COLUMN(cur, 2, r_path,4096); ',
'   DBMS_SQL.DEFINE_COLUMN(cur, 3, r_content_char,32767); ',
'   IF INSTR(p14_query, ''P14_ABOUT'')>0 THEN',
'     dbms_sql.bind_variable(c => cur, name => '':P14_ABOUT'' , value => p14_about );',
'   END IF;',
'   IF INSTR(p14_query, ''P14_VECTOR'')>0 THEN',
'     dbms_sql.bind_variable(c => cur, name => '':P14_VECTOR'' , value => p14_vector );',
'   END IF;',
'   ret := dbms_sql.execute(cur);',
'',
'   prompt := q''^',
'You are a person answering questions in a predefined json format based on 3 rules. The question is: "^'' || p14_question || ''^"  ',
'To respond to the question, follow the 3 following rules:',
'1. Answer the question only based on the json documents below',
'2. If the answer is not found in the content of the documents below, say "I do not find the answer in the documents." ',
'3. The answer has the following json format: { "found": "Boolean response found in document", "document": "Document where the response is found", "response": "response of the question"}',
'',
'JSON document',
'[^'';',
'   bfirst := TRUE;',
'',
'   LOOP ',
'     IF DBMS_SQL.FETCH_ROWS(cur)>0 THEN ',
'        DBMS_SQL.COLUMN_VALUE(cur, 1, r_filename); ',
'        DBMS_SQL.COLUMN_VALUE(cur, 2, r_path); ',
'        DBMS_SQL.COLUMN_VALUE(cur, 3, r_content_char); ',
'        IF bfirst THEN',
'            bFirst := false;',
'        else',
'            prompt := prompt || '','';',
'        end if;',
'        prompt := prompt || ''{ "Document": "''|| r_filename ||''", "Context": "", "Sentence": "''|| r_content_char ||''" }'';',
'      ELSE ',
'        -- No more rows to copy: ',
'        EXIT; ',
'      END IF; ',
'   end loop;',
'   dbms_sql.close_cursor(cur);',
'   prompt := prompt || '']'';',
'   return prompt; ',
'end get_prompt;',
'',
'procedure new_question( a_question varchar2 ) is ',
'begin',
'  APEX_UTIL.SET_SESSION_STATE( ''P14_ABOUT'', ''about(''||a_question||'')'' );',
'  APEX_UTIL.SET_SESSION_STATE( ''P14_VECTOR'', shared_oci.embedText(a_question) );',
'end;',
