' ',
'      log( ''<get_documents> p14_query'', p14_query);',
'      log( ''<get_documents> p14_about'', p14_about);',
'      log( ''<get_documents> p14_question'', p14_question);',
'      log( ''<get_documents> p14_vector'', p14_vector);',
'       ',
'      cur := dbms_sql.open_cursor;',
'      dbms_sql.parse(cur,p14_query,dbms_sql.native);',
'      DBMS_SQL.DEFINE_COLUMN(cur, 1, r_chunck_id, 256); ',
