'           EXIT; ',
'        END IF; ',
'      end loop;',
'      dbms_sql.close_cursor(cur);',
'    end if;',
'    return documents; ',
'end get_documents;',
'',
'function chat2( a_session_id varchar2, question varchar2, type varchar2) return varchar2 is',
'  documents JSON_ARRAY_T := new JSON_ARRAY_T;',
'  chatHistory JSON_ARRAY_T := new JSON_ARRAY_T;',
'  chat JSON_OBJECT_T := new JSON_OBJECT_T;',
'  prompt varchar2(32767);',
'  chat_json varchar2(32767);',
'  chat_result varchar2(32767);',
'  p2_type varchar2(256);',
'  p_path varchar2(256);',
'  p_page number;',
'  counter number;',
'begin',
'  if a_session_id is not null then',
'     chat := new JSON_OBJECT_T;',
'     for rec in (select user_name, comment_text from docs_chat where session_id=a_session_id order by comment_date asc) loop',
'       chat.put(''message'', rec.comment_text);',
'       chat.put(''role'', rec.user_name);',
'       chatHistory.append(chat);',
'     end loop;',
'  end if;',
'',
'  if type=''generate'' then',
'    prompt := question;',
'  else',
'      prompt :=''You are a helpful agent. To respond to the question, follow 2 rules:',
'    1. Answer the question only based in the documents',
'    2. If the answer is not found in the content of the documents below, say "I do not find the answer in the documents. ',
'    The question is: '' || question;',
'      documents := shared_oci.get_documents();',
'  end if;',
'  chat_json := shared_oci.cohere_chat(prompt, documents, chatHistory );',
'  chat_result := JSON_VALUE(chat_json, ''$.chatResponse.text'');',
'',
'  p2_type := APEX_UTIL.GET_SESSION_STATE( ''P2_TYPE'' );',
'  if p2_type = ''rag'' then',
'      -- Documents',
'      chat_result := chat_result || chr(10) || chr(10) || ''Source: '' || chr(10) || ''- Documents: '';',
'      counter := 0;',
'      for rec in (',
'        select distinct t.filename, c.doc_id, c.page from',
'         json_table(chat_json,''$.chatResponse.documents[*]'' COLUMNS ( filename VARCHAR PATH ''$.filename'', chunck_id VARCHAR PATH ''$.chunck_id'' )) t,',
'         v_docs_chunck c',
'         where c.id=hextoraw(t.chunck_id)',
'      ) loop',
'        counter := counter + 1;',
'        if counter>1 then ',
'          chat_result := chat_result || '' / '';',
'        end if;  ',
'         if rec.page = ''0'' or rec.page= '''' or rec.page is null then',
'            chat_result := chat_result || ''['' || rec.filename || '']('' || get_doc_url( rec.doc_id ) || '')'';',
'         else',
'            chat_result := chat_result || ''['' || rec.filename || '' - page '' || rec.page || '']('' || get_doc_url( rec.doc_id ) || ''#page='' || rec.page || '')'';',
'         end if;',
'      end loop;',
'',
'      -- Citations',
'      chat_result := chat_result || chr(10) || ''- Citations: '';',
'      counter := 0;',
'      for rec in (select t.text from json_table(chat_json,''$.chatResponse.citations[*]'' COLUMNS ( text VARCHAR PATH ''$.text'' )) as t) loop',
'        counter := counter + 1;',
'        if counter>1 then ',
'          chat_result := chat_result || '' / '';',
'        end if;  ',
'        chat_result := chat_result || rec.text;',
'      end loop;',
'  end if;',
'  return chat_result;',
'end;',
'',
'FUNCTION get_doc_url( a_id in number ) RETURN VARCHAR2 is',
'  rec_source_type DOCS.source_type%TYPE;',
'  rec_path DOCS.path%TYPE;',
'begin',
'  select source_type, path into rec_source_type, rec_path from docs where id=a_id;',
'  if rec_source_type=''APEX'' then',
'     return ''/ords/vector/ords-viewer/docs/''||a_id;',
'  elsif INSTR( rec_path, ''https://'' )>0 then',
'     return rec_path;',
'  else',
'     -- https://objectstorage.eu-frankfurt-1.oraclecloud.com/n/fr03kzmuvhtf/b/psql-public-bucket/o/image_hello.png#page=0',
'     return ''https://objectstorage.''|| region || ''.oraclecloud.com'' || rec_path;',
'  end if;',
'end;',
'',
'procedure read_config is',
'   l_debug varchar2(256);',
'begin',
'  select namespace, bucket_name, region, compartment_ocid, debug, oci_credential ',
'    into namespace, bucket_name, region, compartment_ocid, l_debug, oci_credential ',
'    from docs_config',
'    where id=1;',
'  debug := (l_debug=''TRUE'');   ',
'  log( ''<read_config> oci_credential'', oci_credential);',
'end;',
'',
'begin',
'  read_config;',
'end;',
'/',
